mapper.select:
select count(*)
from pms_team_member 
where tnm=#{teamName} and mid=#{memberId}

mapper.update:
update pms_team set 
  dscrt=#{description}, 
  max_qty=#{maxQty}, 
  sdt=#{startDate}, 
  edt=#{endDate} 
where name=#{name}

update pms_team set 
  stat=#{state} 
where tano=#{no}
